import React from 'react';
import {Feather} from '@expo/vector-icons';
import {View, Text, Image, TouchableOpacity, Linking} from 'react-native';
import {useNavigation, useRoute} from '@react-navigation/native';
import * as MailComposer from 'expo-mail-composer';
import styles from './styles';
import logoImg from '../../assets/logo.png';

export default function Detail(){
    const navigation = useNavigation();
    const route = useRoute();    
    const incident = route.params.incident;    
    const message = `Ol√° ${incident.name}, caso: ${incident.title}, valor: ${Intl.NumberFormat('pt-BR',{style: 'currency',currency:'BRL'}).format(incident.value)}`;
    
    function navigationBack(){
        navigation.goBack();
    }
    function sendMail(){
        MailComposer.composeAsync({
            subject: `Her√≥i do caso: ${incident.title}`,
            recipients: [incident.email],
            body: message,
        })
    }
    function sendWhatsapp(){
        Linking.openURL(`whatsapp://send?phone=${incident.whatsapp}&text=${message}`);
    }
    return(
        <View style={styles.container}>
            <View style={styles.header}>
                <Image source={logoImg} />
                <TouchableOpacity onPress={navigationBack}>
                    <Feather name="arrow-left" size={28} color="#E82041" />
                </TouchableOpacity>
            </View>
            <View style={styles.incident}>
                <Text style={[styles.incidentProperty, {marginTop:0}]}>curso:</Text>
    <Text style={styles.incidentValue}>{incident.name} de {incident.city}/{incident.uf}</Text>

                <Text style={styles.incidentProperty}>Mat√©rias do curso :</Text>
                <Text style={styles.incidentValue}>{incident.title}</Text>

                <Text style={styles.incidentProperty}>Valor do curso:</Text>
                <Text style={styles.incidentValue}>
                    {
                        Intl.NumberFormat('pt-BR',{
                        style: 'currency', 
                        currency:'BRL'
                        }).format(incident.value)
                    }
                </Text>
            </View>
            <View style={styles.contactBox}>
                <Text style={styles.heroTitle}>Cadastre se j√° </Text>
                <Text style={styles.heroTitle}>Seja aluno SENAI </Text>
                <Text style={styles.heroDescription}>Entre em contato</Text>

                <View style={styles.actions}>
                    <TouchableOpacity style={styles.action} onPress = {sendWhatsapp}>
                        <Text style={styles.actionText}>Whatsapp</Text>
                    </TouchableOpacity>
                    <TouchableOpacity style={styles.action} onPress = {sendMail}>
                        <Text style={styles.actionText}>E-mail</Text>
                    </TouchableOpacity>
                </View>
                
            </View>
        </View>
    );
}
